package com.example.weatherapp.extensionsimport android.app.Applicationimport com.example.weatherapp.di.repositoryModuleimport com.example.weatherapp.di.serializerModuleimport com.example.weatherapp.di.storageModuleimport com.example.weatherapp.di.viewModelModuleimport org.koin.android.ext.koin.androidContextimport org.koin.core.context.startKoinclass AppConfig: Application() {    override fun onCreate() {        super.onCreate()        startKoin {            androidContext(this@AppConfig)            modules(                listOf(                    repositoryModule,                    viewModelModule,                    serializerModule,                    storageModule                )            )        }    }}